//app.js
import React, { useState } from 'react';
import * as pdfjs from 'pdfjs-dist';

pdfjs.GlobalWorkerOptions.workerSrc = process.env.PUBLIC_URL + '/pdf.worker.js';

function App() {
  const [pdf, setPdf] = useState(null);
  const [chapterOnePage, setChapterOnePage] = useState(null);

const onFileChange = async (e) => {
  const file = e.target.files[0];
  console.log("Selected file:", file);  // Log the file details

  if (file) {
    const fileReader = new FileReader();

    fileReader.onload = async (event) => {
      console.log("File loaded:", event.target.result);  // Log the raw result
      const pdfData = new Uint8Array(event.target.result);
      console.log("PDF Data:", pdfData);  // Log the Uint8Array

      const pdfDoc = await pdfjs.getDocument({data: pdfData}).promise;
      for (let i = 1; i <= pdfDoc.numPages; i++) {
        const page = await pdfDoc.getPage(i);
        const content = await page.getTextContent();
        const textItems = content.items.map(item => item.str).join(' ');

        if (textItems.includes("Chapter I")) {
          setChapterOnePage(i);
          break;
        }
      }

     console.log("File reading completed");
      setPdf(pdfData);
    };

    fileReader.readAsArrayBuffer(file);
  }
};
const retrieveUploadedPdf = () => {
  const pdfString = localStorage.getItem('uploadedPdf');
  if (pdfString) {
    const pdfArray = pdfString.split(',').map(byte => parseInt(byte, 10));
    const retrievedPdf = new Uint8Array(pdfArray);
    console.log("Retrieved PDF:", retrievedPdf);
    // Do something with retrievedPdf
  } else {
    console.log("No PDF found in local storage.");
  }
};

const onUpload = () => {
  if (!pdf) {
    console.error("No PDF selected for upload.");
    return;
  }

  try {
    // Convert the Uint8Array to a string for storage
    const pdfString = Array.from(pdf).toString();

    // Store in local storage
    localStorage.setItem('uploadedPdf', pdfString);

    console.log("PDF uploaded locally.");
  } catch (error) {
    console.error("Failed to upload PDF:", error);
  }
};


  return (
    <div className="App">
      <h1>PDF Uploader</h1>
      <input type="file" accept=".pdf" onChange={onFileChange} />
      <button onClick={onUpload}>Upload</button>
      {chapterOnePage && <p>Chapter I starts on page: {chapterOnePage}</p>}
    </div>
  );
}

export default App;
