//app.js
import React, { useState } from 'react';
import * as pdfjs from 'pdfjs-dist';

pdfjs.GlobalWorkerOptions.workerSrc = process.env.PUBLIC_URL + '/pdf.worker.js';

function App() {
  const [pdf, setPdf] = useState(null);
  const [chapterOnePage, setChapterOnePage] = useState(null);
const [chapterPage, setChapterPage] = useState('');
const [chapterNumber, setChapterNumber] = useState('');

const onFileChange = async (e) => {
  const file = e.target.files[0];
  console.log("Selected file:", file);  // Log the file details

  if (file) {
    const fileReader = new FileReader();

 fileReader.onload = async (event) => {
    const pdfData = new Uint8Array(event.target.result);
    setPdf(pdfData);

    // Directly store to local storage
    try {
        const pdfString = Array.from(pdfData).toString();
        localStorage.setItem('uploadedPdf', pdfString);
        console.log("PDF stored locally.");
    } catch (error) {
        console.error("Failed to store PDF:", error);
    }
};

    fileReader.onerror = (error) => {
      console.error("File reading error:", error);  // Log any reading errors
    };

    fileReader.readAsArrayBuffer(file);
  }
};
const findChapterOne = async () => {
     console.log("Starting search for Chapter I...");
    const pdfString = localStorage.getItem('uploadedPdf');
    if (!pdfString) {
        console.error("No PDF found in local storage.");
        return;
    }
    const pdfArray = pdfString.split(',').map(byte => parseInt(byte, 10));
    const retrievedPdf = new Uint8Array(pdfArray);

    // 2. Use pdfjs to parse the PDF and search for "Chapter I"
try {
    const pdfDoc = await pdfjs.getDocument({ data: retrievedPdf }).promise;
    console.log("PDF parsed successfully.");

    const searchTerm = `chapter ${chapterNumber}`.toLowerCase();

    for (let i = 1; i <= pdfDoc.numPages; i++) {
        const page = await pdfDoc.getPage(i);
        const content = await page.getTextContent();
        const textItems = content.items.map(item => item.str.toLowerCase()).join(' ');

        if (textItems.includes(searchTerm)) {
            setChapterPage(i); 
            break;
        }
    }
} catch (error) {
    console.error("Error while parsing or searching the PDF:", error);
} finally {
    // Any cleanup code if needed
    // This block is optional if you don't have specific cleanup tasks.
}

};


const retrieveUploadedPdf = () => {
  const pdfString = localStorage.getItem('uploadedPdf');
  if (pdfString) {
    const pdfArray = pdfString.split(',').map(byte => parseInt(byte, 10));
    const retrievedPdf = new Uint8Array(pdfArray);
    console.log("Retrieved PDF:", retrievedPdf);
    // Do something with retrievedPdf
  } else {
    console.log("No PDF found in local storage.");
  }
};

const onUpload = () => {
  if (!pdf) {
    console.error("No PDF selected for upload.");
    return;
  }

  try {
    // Convert the Uint8Array to a string for storage
    const pdfString = Array.from(pdf).toString();

    // Store in local storage
    localStorage.setItem('uploadedPdf', pdfString);

    console.log("PDF uploaded locally.");
  } catch (error) {
    console.error("Failed to upload PDF:", error);
  }
};


  return (
    <div className="App">
      <h1>PDF Uploader</h1>
      <input type="file" accept=".pdf" onChange={onFileChange} />
      <button onClick={onUpload}>Upload</button>
<input 
    type="number" 
    placeholder="Enter Chapter Number" 
    value={chapterNumber} 
    onChange={(e) => setChapterNumber(e.target.value)}
/>

<button onClick={findChapterOne}>Search Chapter</button>


{chapterPage && <p>Chapter {chapterNumber} starts on page: {chapterPage}</p>}

    </div>
  );
}

export default App;
